  #if USE_EMISSIVE
    o.rgb += s.emissive;
  #endif
  #if USE_SPECULAR
    o.rgb += lighting.specular * s.specular;
  #endif
  #if CC_USE_SHADOW_MAP
    o.rgb *= CCCalcShadow();
  #endif
