  vec4 diffuseTexture_tmp = texture2D(diffuseTexture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_diffuseTexture
      diffuseTexture_tmp.a *= texture2D(diffuseTexture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    diffuse.rgb *= (diffuseTexture_tmp.rgb * diffuseTexture_tmp.rgb);
    diffuse.a *= diffuseTexture_tmp.a;
  #else
    diffuse *= diffuseTexture_tmp;
  #endif
  #endif
  ALPHA_TEST(diffuse);
  s.diffuse = diffuse.rgb;
  s.opacity = diffuse.a;
  s.emissive = emissiveColor.rgb;
  #if USE_EMISSIVE_TEXTURE
  vec4 emissiveTexture_tmp = texture2D(emissiveTexture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_emissiveTexture
      emissiveTexture_tmp.a *= texture2D(emissiveTexture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    s.emissive.rgb *= (emissiveTexture_tmp.rgb * emissiveTexture_tmp.rgb);
  #else
    s.emissive.rgb *= emissiveTexture_tmp.rgb;
  #endif
  #endif
  s.specular = specularColor.rgb;
  #if USE_SPECULAR_TEXTURE
  vec4 specularTexture_tmp = texture2D(specularTexture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_specularTexture
      specularTexture_tmp.a *= texture2D(specularTexture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    s.specular.rgb *= (specularTexture_tmp.rgb * specularTexture_tmp.rgb);
  #else
    s.specular.rgb *= specularTexture_tmp.rgb;
  #endif
  #endif
  s.normal = v_worldNormal;
  #if USE_NORMAL_TEXTURE
    vec3 nmmp = texture2D(normalTexture, v_uv0).xyz - vec3(0.5);
    s.normal =
      nmmp.x * normalize(v_tangent) +
      nmmp.y * normalize(v_bitangent) +
      nmmp.z * normalize(s.normal);
  #endif
